function [7:0] degree;
   input [255:0]  data;
   reg   [7:0]    i;
   begin
      i=8'b0;
      while((data>>1)>0)
      begin
         data=(data>>1);
         i=i+8'b1;
      end
      degree=i;
   end
endfunction

function    [23:0]      crc_func_0;
   input    [255:0]     data;
   reg      [255:0]     poly;
   reg      [7:0]       dataDegree;
   reg      [7:0]       polDegree;
   begin
      poly = {{233'h0},{23'h5d6dcb}};
      polDegree=degree(poly);
      data=(data<<polDegree);
      dataDegree=degree(data);
      while(dataDegree > polDegree)
      begin
         poly = {{233'h0},{23'h5D6DCB}};
         poly=(poly<<(dataDegree-polDegree));
         data=(data^poly);
         dataDegree=degree(data);
      end
      crc_func_0=data[23:0];
   end
endfunction

function    [23:0]      crc_func_1;
   input    [255:0]     data;
   reg      [255:0]     poly;
   reg      [7:0]       dataDegree;
   reg      [7:0]       polDegree;
   begin
      poly = {{232'h0},{24'h864cfb}};
      polDegree=degree(poly);
      data=(data<<polDegree);
      dataDegree=degree(data);
      while(dataDegree > polDegree)
      begin
         poly = {{232'h0},{24'h864cfb}};
         poly=(poly<<(dataDegree-polDegree));
         data=(data^poly);
         dataDegree=degree(data);
      end
      crc_func_1=data[23:0];
   end
endfunction

function [63:0] timestamp;
   input [31:0]  counter;   
   input [31:0]  carry_out;
   begin
      timestamp = ((carry_out)*(2**32-1) + counter)*TIMECLK;

   end
